import React, {Component} from "react";
import { Menu, Layout, message, Drawer, Space, Button, Divider, Form, InputNumber, Input, Select, List, Avatar, Skeleton } from 'antd';
import { UserOutlined, LaptopOutlined, FileSearchOutlined, HomeOutlined, EyeTwoTone, EyeInvisibleOutlined, RocketOutlined } from '@ant-design/icons';
import cookie from "react-cookies";
//接入web3
import getWeb3 from "../../../utils/getWeb3";
import getEcommerceStore from "../../../utils/getEcommerceStore";
import openNotification from "../../Notification";

const { Option } = Select;

const count = 6;
const fakeDataUrl = `https://randomuser.me/api/?results=${count}&inc=name,gender,email,nat,picture&noinfo`;




class Sell extends Component {

    state = {
        visible: false,
        //list
        initLoading: true,
        loading: false,
        data: [],
        list: [],
    }

    constructor (props) {
        super (props);
    }

    async componentDidMount () {
        console.log("生成Sell模块");

        //注意回调函数中的this，和外界的this不同，如果向使用外界的this，要赋值成that传过去
        //注意要使用async，确保先拿到地址，用await关键字确保运行的先后顺序，再用这个地址调用合约方法
        let that = this;
        this.props.truffleContract.deployed().then( async (i) => {
            console.log("进入 this.props.truffleContract.deployed() 的回调函数");
            try{
                //即时获取当前地址，用该地址发交易
                let currentAccount = await that.props.web3.eth.getAccounts();

                //ID
                let blockChainID = this.props.dataArray[0].ID;
                console.log("发货商品ID : " + blockChainID);

                i.orderInfo(blockChainID, { from: currentAccount.toString()}).then(async res => {
                    //可能返回为空，要判断是否真的取到了数据，如果不判断，直接调用 formatProductInfo(res)，里面的 ipfs 会报错
                    console.log("orderInfo 取到的原始数据为 : ");
                    console.dir(res);

                    // //格式化数据，便于显示
                    // let oneProductInfo = await this.formatProductInfo(res);
                    // console.log("格式化之后的数据 oneProductInfo 为 ");
                    // console.dir(oneProductInfo);

                    // this.setState({
                    //     dataArray : [...this.state.dataArray, oneProductInfo]
                    // });

                });  
            }catch(err) {
                message.error("商品ID查询失败",2);
                console.log("调用合约的getGoods方法失败 " + err);
            }


        });

        fetch(fakeDataUrl)
      .then(res => res.json())
      .then(res => {
        this.setState({
          initLoading: false,
          data: res.results,
          list: [],
        });
      });
    }

    showDrawer = () => {
        this.setState({
          visible: true,
        });
      };

    onClose = () => {
        this.setState({
          visible: false,
        });
      };

      onLoadMore = () => {
        this.setState({
          loading: true,
          list: this.state.data.concat(
            [...new Array(count)].map(() => ({ loading: true, name: {}})),
          ),
        });
        fetch(fakeDataUrl)
          .then(res => res.json())
          .then(res => {
            const data = this.state.data.concat(res.results);
            this.setState(
              {
                data,
                list: data,
                loading: false,
              },
              () => {
                // Resetting window's offsetTop so as to display react-virtualized demo underfloor.
                // In real scene, you can using public method of react-virtualized:
                // https://stackoverflow.com/questions/46700726/how-to-use-public-method-updateposition-of-react-virtualized
                window.dispatchEvent(new Event('resize'));
              },
            );
          });
      };



    render () {


      const { initLoading, loading, list } = this.state;
    const loadMore =
      !initLoading && !loading ? (
        <div
          style={{
            textAlign: 'center',
            marginTop: 12,
            height: 32,
            lineHeight: '32px',
          }}
        >
          <Button onClick={this.onLoadMore}>loading more</Button>
        </div>
      ) : null;

        return (


          <div>

            <Button type="primary" icon={<RocketOutlined />} size={"large"} onClick={this.showDrawer}>
                发货
            </Button>

            <Drawer
          title="发货"
          width={400}
          onClose={this.onClose}
          visible={this.state.visible}
          bodyStyle={{ paddingBottom: 80 }}
          extra={
            <Space>
              <Button onClick={this.onClose}>取消</Button>
            </Space>
          }
        >
            <Divider />
          <p>读取买家的信息，后面有个发货按钮，信息上链，从合约拿钱，写入溯源合约</p>

          <List
        className="demo-loadmore-list"
        loading={initLoading}
        itemLayout="horizontal"
        loadMore={loadMore}
        dataSource={list}
        renderItem={item => (
          <List.Item
            actions={[<a key="list-loadmore-more">确认</a>]}
          >
            <Skeleton avatar title={false} loading={item.loading} active>
              <List.Item.Meta
                title={<a href="https://ant.design">{111}</a>}
                description="Ant Design, a design language for background applications, is refined by Ant UED Team"
              />
              <div>内容</div>
            </Skeleton>
          </List.Item>
        )}
      />


        </Drawer>

            </div>


        );
    }
}

export default Sell;
