import React, {Component} from "react";
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
// import './index.css';
import { List, Avatar, Space } from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';



const IconText = ({ icon, text }) => (
  <Space>
    {React.createElement(icon)}
    {text}
  </Space>
);



class ProductList extends Component {
  //传入 productArray
  //结构为
  // auctionEndTime: 1646381747
  // auctionStartTime: 1646381347
  // blockchainId: 1
  // category: "Cell Phones & Accessories"
  // condition: 0
  // ipfsDescHash: "QmRa3BnvMUU6DxnLzxymAd7dweyWAYLzW3R99KkGCJAgRn"
  // ipfsImageHash: "QmZsb9nLACC5zGhZp8ezeA9xMfikGQ6LSu3V3gTVnibeJa"
  // name: "iphone 5"
  // price: 2000000000000000000
  // productStatus: 0
  // __v: 0
  // _id: "6221c928b1126364e456764e"

  constructor (props) {
    super(props);
  }

  listData = [];


  componentWillMount  = () => {
    let productListLength = this.props.productArray.length;
    console.log("即将渲染的产品数量为 : ", productListLength);
    for (let i = 0; i < 12; i++) {
      this.listData.push({
        href: 'https://ant.design',
        title: `ant design part ${i}`,
        avatar: 'https://joeschmoe.io/api/v1/random',
        description:
          'Ant Design, a design language for background applications, is refined by Ant UED Team.',
        content:
          'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
      });
    }
  }


    render () {

        return (
            <List
    itemLayout="vertical"
    size="large"
    pagination={{
      onChange: page => {
        console.log(page);
      },
      pageSize: 5,
    }}
    dataSource={this.listData}
    footer={
      <div>
        <b>ant design</b> footer part
      </div>
    }
    renderItem={item => (
      <List.Item
        key={item.title}
        actions={[
          <IconText icon={StarOutlined} text="156" key="list-vertical-star-o" />,
          <IconText icon={LikeOutlined} text="156" key="list-vertical-like-o" />,
          <IconText icon={MessageOutlined} text="2" key="list-vertical-message" />,
        ]}
        extra={
          <img
            width={272}
            alt="logo"
            src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
          />
        }
      >
        <List.Item.Meta
          avatar={<Avatar src={item.avatar} />}
          title={<a href={item.href}>{item.title}</a>}
          description={item.description}
        />
        {item.content}
      </List.Item>
    )}
    />
        );
    }
}

export default ProductList;
