import React, {Component} from 'react';
import axios from "axios";
import cookie from 'react-cookies';
import ipfsAPI from 'ipfs-api';
// import EditDeviceModal from "../../EditDeviceModal";
import { message, Card} from 'antd';
import {
    Form,
    Input,
    InputNumber,
    Cascader,
    Select,
    Row,
    Col,
    Checkbox,
    Button,
    Upload,
    AutoComplete,
    DatePicker,
    Slider,
  } from 'antd';
// import axios from "axios";
// import cookie from "react-cookies";

import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
// import './index.css';
import { List, Avatar, Space} from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';

import { UploadOutlined, InboxOutlined } from '@ant-design/icons';

//接入合约
import getWeb3 from "../../../utils/getWeb3";
import getEcommerceStore from "../../../utils/getEcommerceStore";

//静态资源
import ProductClass from '../../../static/ProductClass';

//ipfs配置文件
import ipfsAddAndCatConfig from '../../../config/ipfsAddAndCat';

const { Option } = Select;
const { RangePicker } = DatePicker;


const IconText = ({ icon, text }) => (
    <Space>
      {React.createElement(icon)}
      {text}
    </Space>
  );

//ipfs实例
const ipfs = ipfsAPI({ host: ipfsAddAndCatConfig.host, port: ipfsAddAndCatConfig.port, protocol: ipfsAddAndCatConfig.protocol });


//从区块链读出某件商品信息
class ProductInfo extends Component {


    state = {
        web3 : null,
        truffleContract : null,
        dataArray : []
    }

    constructor (props) {
        super(props);
    }

    getBlockChainInfo = async() => {
        try{
            const web3 = await getWeb3();
            const EcommerceStore = await getEcommerceStore(web3);
            this.setState({
                web3: web3,
                truffleContract:EcommerceStore
            });
            message.success("接入智能合约成功", 2);
            console.log("ProductInfo 的 web3 : ");
            console.dir(web3);
            console.log("ProductInfo 的 EcommerceStore : ");
            console.dir(this.state.truffleContract);
            return true;
        }catch (error) {
            // Catch any errors for any of the above operations.
            alert(
              `[ERROR]接入智能合约失败.`,
            );
            console.error(error);
            return false;
          }

    }

    //componentWillMount在render之前运行
    //componentDidMount 在render之后运行
    componentDidMount () {
    // componentWillMount () {
        console.log("生成ProductInfo模块");
        if(this.getBlockChainInfo()){
            //注意，这里打印 this.state.truffleContract 为 null，因为异步执行
            // console.log("成功获得合约信息, this.state.truffleContract : ");
            // console.dir(this.state.truffleContract);
            console.log("获得合约信息成功");
        }else{
            console.log("获得合约信息失败");
        }
    }

    //点击提交按钮，表单可以提交时
    onFinish = (values) => {

        console.log('onFinish 的 values : ', values);

        let decodedParams = {};
        Object.keys(values).forEach(key => {
            decodedParams[key] = decodeURIComponent(decodeURI(values[key]));
        });
        // console.log('onFinish 的 values 解析为 decodedParams : ', decodedParams);

        //decodedParams 进一步格式化
        //参数全都变成String

        message.info("正在提交查询数据",2);
        try{
            // this.saveProduct(decodedParams);

            //调用之前就隐藏
            // $("#bidding, #revealing, #finalize-auction, #escrow-info").hide();
            this.renderProductDetails(decodedParams);
        }catch(err){
            console.log("在区块链中查询失败" + err);
            return;
        }
    }


    //点击提交按钮，表单不能提交时
    onFinishFailed = (errorInfo) => {
        message.warning("请正确填写商品ID信息",2);
    }

    //调用区块链方法
    renderProductDetails = (params) => {

        if(this.state.truffleContract === null || this.state.web3 === null){
            message.error("连接合约失败",2);
            console.log("[ERROR]web3 或者 truffleContract 为 null");
            return;
        }

        //传来的数据已格式化为
        //params.ProductID (String)

        console.log("用来查询的信息 : ");
        console.log(params);

        console.log("合约为 : ");
        console.dir(this.state.truffleContract);


        //注意回调函数中的this，和外界的this不同，如果向使用外界的this，要赋值成that传过去
        //注意要使用async，确保先拿到地址，用await关键字确保运行的先后顺序，再用这个地址调用合约方法
        let that = this;
        console.dir(this.state.truffleContract.deployed());
        this.state.truffleContract.deployed().then(async (i) => {
            console.log("进入 this.state.truffleContract.deployed() 的回调函数");
            //测试时要在MetaMask中选中ganache提供的10个地址之一，from自己创建的地址会失败
            //即时获取当前地址，用该地址发交易
            let currentAccount = await that.state.web3.eth.getAccounts();
            message.info("交易发起地址为: "+currentAccount, 2);
            console.log("交易发起地址为: "+currentAccount);

            try{
                //合约中实现事件event NewProduct监听
                await i.addProductToStore({ from: currentAccount.toString(), gas: 440000 }).then(function(f) {
                    //注意这里调用合约方法使用的地址，必须是字符串形式，要用 toString() 转化为字符串

                    message.success("向链上提交商品数据成功",2);
                    console.log("成功调用合约的addProductToStore方法");
                    console.log(f);
                });  
            }catch(err) {
                message.error("向链上提交商品数据失败",2);
                console.log("调用合约的addProductToStore方法失败 " + err);
            }


        });
    }


    render () {

        if(true){
            return (
                <div style={{display:'flex', justifyContent:'center'}}>
                <Card title="查询商品" bordered={false} style={{ width: '80%' }} headStyle={{display:'flex', justifyContent:'center'}}>
                    <Form
                        initialValues={{
                            remember: true,
                        }}
                        onFinish={this.onFinish}
                        onFinishFailed={this.onFinishFailed}
                        style={{margin:"auto", width:"60%", marginTop:"15px"}}
                    >
                        <Form.Item
                            name="ProductID"
                            rules={[
                                {
                                    required: true,
                                    message: '请输入商品ID',
                                },
                            ]}
                        >
                            <Input placeholder="商品ID"/>
                        </Form.Item>



                        <Form.Item
                            wrapperCol={{
                                offset: 10,
                                span: 16,
                            }}
                        >
                            <Button disabled={ this.state.truffleContract === null || this.state.web3 === null } type="primary" htmlType="submit">
                                查询
                            </Button>
                        </Form.Item>
                    </Form>
                </Card>
            </div>
            );
        }
        if(true){
            return (
                <List
        itemLayout="vertical"
        size="large"
        pagination={{
          onChange: page => {
            console.log(page);
          },
          pageSize: 5,
        }}
        dataSource={this.state.dataArray}
        footer={
          <div>
            共 {this.state.dataArray.length} 个
          </div>
        }
        renderItem={item => (
          <List.Item
            key={item.index}
            actions={[
              <IconText icon={StarOutlined} text={item.price} key="list-vertical-star-o" />,
              <IconText icon={LikeOutlined} text={item.productCondition} key="list-vertical-like-o" />,
              <IconText icon={MessageOutlined} text={item.processTime} key="list-vertical-message" />,
            ]}
            extra={
              <img
                width={130}
                height={130}
                alt="商品图片"
                src={item.imageUrl}
              />
            }
          >
            <List.Item.Meta
              // avatar={<Avatar src={item.avatar} />}
              title={<a href={item.href}>{item.name}</a>}
              description={item.category}
            />
            {(item.descString.length > 150) ? item.descString.slice(0,150)+"......"  : item.descString}
          </List.Item>
        )}
        />
            );
        }

    }
}

export default ProductInfo;
