import React, { useState } from 'react';
import ProCard, { StatisticCard } from '@ant-design/pro-card';
import RcResizeObserver from 'rc-resize-observer';

const { Statistic } = StatisticCard;

function ProductsChart ()  {
  const [responsive, setResponsive] = useState(false);

  return (
    <RcResizeObserver
      key="resize-observer"
      onResize={(offset) => {
        setResponsive(offset.width < 596);
      }}
    >
      <ProCard split={responsive ? 'horizontal' : 'vertical'}>
        <StatisticCard
          colSpan={responsive ? 24 : 6}
          title="资金托管合约总流水"
          statistic={{
            value: 1000000,
            suffix: 'ETH',
            description: <Statistic title="日同比" value="10000000%" trend="up" />,
          }}
          chart={
            <img
              src="https://gw.alipayobjects.com/zos/alicdn/PmKfn4qvD/mubiaowancheng-lan.svg"
              alt="进度条"
              width="100%"
            />
          }
          footer={
            <>
              <Statistic value="100000000%" title="撮合成功占比" layout="horizontal" />
            </>
          }
        />
        <StatisticCard.Group
          colSpan={responsive ? 24 : 18}
          direction={responsive ? 'column' : undefined}
        >
          <StatisticCard
            statistic={{
              title: '总商品数',
              value: 1000000000000,
              description: <Statistic title="日同比" value="100000000000%" trend="up" />,
            }}

          >
            <p>  </p> 
            <Statistic
              title="您发布的商品数"
              value={1000000000000}
              layout="vertical"
              description={<Statistic title="日同比" value="1000000000000%" trend="down" />}
            />
          </StatisticCard>
          <StatisticCard
            statistic={{
              title: '总出价次数',
              value: 1000000000000,
              description: <Statistic title="日同比" value="1000000000000%" trend="down" />,
            }}

          >
            <p>  </p> 
            <Statistic
              title="您进行出价的商品数"
              value={1000000000000}
              layout="vertical"
              description={<Statistic title="日同比" value="1000000000000%" trend="up" />}
            />
          </StatisticCard>
          <StatisticCard
            statistic={{
              title: '活跃地址数',
              value: 1000000000000,
              description: <Statistic title="日同比" value="1000000000000%" trend="up" />,
            }}

          >
            <p>  </p> 
            <Statistic
              title="您的仲裁收入"
              value={1000000000000}
              layout="vertical"
              description={<Statistic title="日同比" value="1000000000000%" trend="down" />}
            />
          </StatisticCard>
        </StatisticCard.Group>
      </ProCard>
    </RcResizeObserver>
  );
};
export default ProductsChart;
