        oneProductInfo.auctionRevealTime = parseInt(auctionRevealTime);

        oneProductInfo.sellStatus = (originInfo[7] ===0) ? "正在拍卖" : ((originInfo[7] ===1) ? "已售出" : "未售出");
        return oneProductInfo;
