import React, {Component} from "react";
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
// import './index.css';
import { List, Avatar, Space } from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';



const IconText = ({ icon, text }) => (
  <Space>
    {React.createElement(icon)}
    {text}
  </Space>
);



class ProductList extends Component {
  state = {
    dataArray : []
  }

  //////////////////////重要
  //this.props.productArray 为 promise
  //promise需要解析才能使用
  //要用.then拿promise的结果
  //
  // key
  // name
  // category
  // descString
  // imageUrl
  // productCondition
  // price
  // processTime


  constructor (props) {
    super(props);
  }

  productListData = [];


  //应该在调用 ProductList 的函数中，将数据格式化好，在 ProductList 中直接将数据放入 render，让 render 自动刷新
  componentWillMount  = () => {

    this.props.productArray.then(res =>{

      console.log("promise.then 后拿取的信息为 : ");
      console.dir(res);
      this.setState({dataArray : res});


    }).catch( err =>{
      console.log("产品展示列表不能获得产品信息" + err);
    })


    //模拟数据，用来测试
    for (let i = 0; i < 2; i++) {
      this.productListData.push({
        href: 'https://ant.design',
        name: '商品名称',
        avatar: 'https://joeschmoe.io/api/v1/random',
        category:
          '商品类型',
        descString:
          '商品介绍',
        price:
          '1 ETH',
        processTime:
          '时间',
        productCondition:
          '全新',
      });

    }

  }

  componentDidMount = () => {

  }


    render () {

      console.log("ProductList 进行了一次渲染");

        return (
            <List
    itemLayout="vertical"
    size="large"
    pagination={{
      onChange: page => {
        console.log(page);
      },
      pageSize: 5,
    }}
    dataSource={this.productListData}
    footer={
      <div>
        共 num 个
      </div>
    }
    renderItem={item => (
      <List.Item
        key={item.index}
        actions={[
          <IconText icon={StarOutlined} text={item.price} key="list-vertical-star-o" />,
          <IconText icon={LikeOutlined} text={item.productCondition} key="list-vertical-like-o" />,
          <IconText icon={MessageOutlined} text={item.processTime} key="list-vertical-message" />,
        ]}
        extra={
          <img
            width={272}
            alt="商品图片"
            src={item.imageUrl}
          />
        }
      >
        <List.Item.Meta
          // avatar={<Avatar src={item.avatar} />}
          title={<a href={item.href}>{item.name}</a>}
          description={item.category}
        />
        {item.descString}
      </List.Item>
    )}
    />
        );
    }
}

export default ProductList;
