import React, {Component} from 'react';
import {Typography, Breadcrumb, Button, Layout, Menu, message} from 'antd'
import DevicesChart from "../DevicesChart";
import Footer from "../../components/Footer";
import LeftMenu from "../../components/LeftMenu";
import PersonalInfo from "../PersonalInfo";
import AlterPwd from "../AlterPwd";
import CreateDevice from "../createDevice";
import EditDevice from "../EditDevice";
import queryMessages from "../queryMessage";
import StaticsReport from "../StatisticsReport";
import './index.css'
import cookie from "react-cookies";
import {Route, Switch} from "react-router-dom";
import axios from "axios";
// import { ContactsOutlined } from "@ant-design/icons";

import getWeb3 from "../../utils/getWeb3";
import getEcommerceStore from "../../utils/getEcommerceStore";


const { Header, Content } = Layout;
const { Title } = Typography;




export default class Index extends Component {

    state = {//当前页面
            currentPage:"",
            username:cookie.load('username'),
            //WebSocket
            ws:null,
            //当前账户
            currentAccount:"",
            //当前合约
            currentContract:null
        }

    //是否需要登录
    constructor (props) {
        super (props);
        const username = cookie.load('username');
        //防止输入URL直接进入主页
        if(username === undefined) {
            window.location.href = '/login'
        }
    }

    //用户退出
    handleLoginOut = () => {
        //清除cookie
        cookie.remove('username', { path: '/' })
        cookie.remove('loginSuccess', { path: '/' })
        cookie.remove('webSocket', {path:'/'})
        cookie.remove('email', {path:'/'})
        const ws = this.state.ws;
        //关闭连接
        if(ws !== null) ws.close();
        window.location.href = '/login';
    }

    //切换页面
    changePage = name => {
        this.setState({currentPage:name});
    }

    //
    getMyDevice = () => {
        axios.post("/getPersonalInfo", {
            username:this.state.username
        }).then(response => {
            const data =  response.data;
            if(data.code === "success"){
                let array = data.deviceArray.map((item, index) => {
                    return {
                        deviceName: item.deviceName,
                        deviceID: item.deviceID
                    }
                });

                cookie.save('deviceArray', array, {path:'/'});
            }
            else{
                message.warning ("获取个人信息出错").then (r  => console.log(r));
            }
        })
    }

    //
    getBlockChainInfo = async() => {
        try {
            //使用 await，等待上一步的结果
            // Get network provider and web3 instance. 
            //动态获取
            const web3 = await getWeb3();
            console.log("运行 getWeb3() 成功");
            console.dir(web3);
            // Use web3 to get the user's accounts.
            let accounts = await web3.eth.getAccounts();
            console.log("当前 eth地址 为: " + this.state.currentAccount);
            // Get the contract instance.
            const networkId = await web3.eth.net.getId();
            console.log("当前 networkId 为: " + networkId);

            //由truffle生成合约实例
            //动态获取
            const EcommerceStore = await getEcommerceStore(web3);

            // const deployedNetwork = EcommerceStore.networks[networkId];
            // console.log("当前 deployedNetwork 为: ");
            // console.dir(deployedNetwork);

            console.log("当前 EcommerceStore 为: ");
            console.dir(EcommerceStore);

          //   //由web3生成合约实例
          //   const instance = new web3.eth.Contract(
          //     SimpleStorageContract.abi,
          //     deployedNetwork && deployedNetwork.address,
          //   );
          //   console.log("instance: ");
          //   console.dir(instance);
          //   // Set web3, accounts, and contract to the state, and then proceed with an
          //   // example of interacting with the contract's methods.
          //   this.setState({ web3, accounts, contract: instance });
            this.setState({currentAccount:accounts});

            console.log("state.currentAccount: "+this.state.currentAccount);
            console.log("state.currentPage: "+this.state.currentPage);
            console.log("state.username: "+this.state.username);
            console.log("state.ws: "+this.state.ws);

            return true;

          } catch (error) {
            // Catch any errors for any of the above operations.
            alert(
              `[ERROR]接入智能合约失败.`,
            );
            console.error(error);
            return false;
          }
    }

    componentDidMount () {
        console.log("生成首页");
        if(this.getBlockChainInfo()){
            console.log("成功获得合约信息");
        }else{
            console.log("获得合约信息失败");
        }


        //从后端获取商品总体信息

        // try {
        //     this.getMyDevice();
        // }catch (err){
        //     console.log("获得商品信息失败");
        // }
        // console.log("成功获得商品信息");


        // 连接后端 app.js 中的 ws

        // const webSocket = cookie.load('webSocket');
        // if(webSocket === undefined){
        //     // 打开一个 web socket  这里端口号和监听的需一致
        //     const ws = new WebSocket ('ws://localhost:4000');
        //     ws.onopen = function(e){
        //         console.log("连接服务器成功");
        //         ws.send('hello');
        //         // 向服务器发送消息
        //         ws.send("test");
        //     }
        //     ws.onclose = (e) => {
        //         this.setState({ws:null});
        //         cookie.remove('webSocket');
        //         console.log('websocket连接关闭')
        //     }
        //     // 这里接受服务器端发过来的消息
        //     ws.onmessage = function(e) {
        //         console.log(e.data);
        //         const array = cookie.load('deviceArray');
        //         if(e.data.indexOf("上线") !== -1){
        //             array.forEach(item => {
        //                 if(e.data.indexOf(item.deviceID) !== -1){
        //                     const str = e.data.replace(item.deviceID, "");
        //                     message.success( str, 10)
        //                         .then(value => console.log(value), reason => console.log(reason))
        //                 }
        //             })
        //         }
        //         else{
        //             array.forEach(item => {
        //                 if(e.data.indexOf(item.deviceID) !== -1){
        //                     const str = e.data.replace(item.deviceID, "");
        //                     message.warning( str, 10)
        //                         .then(value => console.log(value), reason => console.log(reason))
        //                 }
        //             })
        //         }
        //     }
        //     this.setState({ws:ws});
        //     // cookie.save('webSocket', true, {path:'/'});
        //     cookie.save('webSocket', ws, {path:'/'});
        // }

    }

    render () {
        return (
            <div>
            <Layout>
                <Header className="header">
                    <Menu theme="dark" mode="horizontal" id="banner">
                        <div id="myTitle"><Title style={{color:"white"}} level={3}>区块链电子商务平台</Title></div>
                        <Button type="primary" id="exitBtn" onClick = {this.handleLoginOut}>退出登录</Button>
                    </Menu>
                </Header>
                <Layout>
                    <LeftMenu changePag={this.changePage}/>
                    <Layout style={{ padding: '0 24px 24px', position:'relative' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item>首页</Breadcrumb.Item>
                            <Breadcrumb.Item>{this.state.currentPage}</Breadcrumb.Item>
                        </Breadcrumb>
                        <Content
                            className="site-layout-background"
                            style={{
                                padding: 24,
                                margin: 0,
                                minHeight: "90vh",
                            }}
                        >
                            <Switch>
                                <Route exact path={"/index"} component={DevicesChart}/>
                                <Route exact path={"/index/personalInfo"} component={PersonalInfo}/>
                                <Route exact path={"/index/alterPwd"} component={AlterPwd}/>
                                <Route exact path={"/index/createDevice"} component={CreateDevice}/>
                                <Route exact path={"/index/editDevice"} component={EditDevice}/>
                                <Route exact path={"/index/queryMessage"} component={queryMessages}/>
                                <Route exact path={"/index/staticsReport"} component={StaticsReport}/>
                            </Switch>
                            <Footer/>
                        </Content>
                    </Layout>
                </Layout>
            </Layout>

            </div>
        );
    }
}
