import React, {Component} from 'react';
import axios from "axios";
import cookie from 'react-cookies';
import ipfsAPI from 'ipfs-api';
// import EditDeviceModal from "../../EditDeviceModal";
import { message, Card} from 'antd';
import {
    Form,
    Input,
    InputNumber,
    Cascader,
    Select,
    Row,
    Col,
    Checkbox,
    Button,
    Upload,
    AutoComplete,
    DatePicker,
    Slider,
  } from 'antd';
// import axios from "axios";
// import cookie from "react-cookies";

import { UploadOutlined, InboxOutlined } from '@ant-design/icons';

//接入合约
import getWeb3 from "../../../utils/getWeb3";
import getEcommerceStore from "../../../utils/getEcommerceStore";

//静态资源
import ProductClass from '../../../static/ProductClass';

//ipfs配置文件
import ipfsConfig from '../../../config/ipfs';

const { Option } = Select;
const { RangePicker } = DatePicker;

//ipfs实例
const ipfs = ipfsAPI({ host: ipfsConfig.host, port: ipfsConfig.port, protocol: ipfsConfig.protocol });


  const suffixSelector = (
    <Form.Item name="suffix" noStyle>
      <Select
        style={{
          width: 70,
        }}
      >
        <Option value="USD">$</Option>
        <Option value="CNY">¥</Option>
      </Select>
    </Form.Item>
  );

  const rangeConfig = {
    rules: [
      {
        type: 'array',
        required: true,
        message: '请选择时间',
      },
    ],
  };




//向区块链写入商品信息
class AuctionOut extends Component {

    state = {
        username:cookie.load('username'),
        web3 : null,
        truffleContract : null,
        //读取上传的图片
        picReader : null
    }

    constructor (props) {
        super(props);
    }

    //当文件选择变化时
    //getValueFromEvent调用它
    normFile = (e) => {
        console.log("调用上传图片的 getValueFromEvent");
        console.log(e);
        const file = e.fileList[0].originFileObj;
        this.PicReader = new window.FileReader();
        this.PicReader.readAsArrayBuffer(file);
        console.log("图片读取为 : "+file);
      }

    //customRequest调用它
    uploadFile = (e) => {
        // message.info("正在上传图片", 2);
        // console.log("调用上传图片的 customRequest");
        // console.log(e);
        // const file = e.target.files[0];
        // this.PicReader = new window.FileReader();
        // this.PicReader.readAsArrayBuffer(file);
        // console.log("图片读取为 : "+this.PicReader);
      }



    getBlockChainInfo = async() => {
        try{
            const web3 = await getWeb3();
            const EcommerceStore = await getEcommerceStore(web3);
            this.setState({
                web3: web3,
                truffleContract:EcommerceStore
            });
            message.success("接入智能合约成功", 2);
            return true;
        }catch (error) {
            // Catch any errors for any of the above operations.
            alert(
              `[ERROR]接入智能合约失败.`,
            );
            console.error(error);
            return false;
          }

    }

    componentDidMount () {
        console.log("生成AuctionOut模块");
        if(this.getBlockChainInfo()){
            console.log("成功获得合约信息");
        }else{
            console.log("获得合约信息失败");
        }
    }

    onFinish = (values) => {
        console.log('Success:', values);
        //ProductName
        message.info("正在提交拍卖商品数据");


        axios.post('/createDevice', {
            deviceID:values.deviceID,
            deviceName:values.deviceName,
            ownerName:cookie.load('username')
        }).then(response => {
            const data = response.data;
            if(data.status === "success"){
                message.success("设备创建成功", 2);
                let deviceArray = cookie.load('deviceArray');
                cookie.remove('deviceArray', {path:'/'});
                deviceArray = [...deviceArray, values.deviceID];
                cookie.save('deviceArray', deviceArray, {path:'/'});

            }
            else if(data.status === "duplicated"){
                message.warning("该设备已被绑定", 2)
            }
            else{
                message.warning("创建设备失败", 2)
            }
        })

    }
    onFinishFailed = (errorInfo) => {
        message.warning("请正确填写商品信息",2);
    }


    //向区块链保存商品入口函数
    saveProduct = (reader, decodedParams) => {
        let imageId, descId;
        this.saveImageOnIpfs(reader).then(function(id) {
            imageId = id;
            this.saveTextBlobOnIpfs(decodedParams["product-description"]).then(function(id) {
                descId = id;
                this.saveProductToBlockchain(decodedParams, imageId, descId);
            });
        });
    }

    //图片上传ipfs并获取hash
    saveImageOnIpfs = (reader) => {
        return new Promise((resolve, reject) => {
            let buffer = Buffer.from(reader.result);
            ipfs.add(buffer).then(res => {
                console.log("ImgRes: ", res);
                resolve(res[0].hash);
            }).catch(err => {
                console.error(err);
                reject(err);
            });
        });
    }

    //简介上传ipfs并获取hash
    saveTextBlobOnIpfs = (blob) => {
        return new Promise((resolve, reject) => {
            let buffer = Buffer.from(blob, 'utf-8');
            ipfs.add(buffer).then(res => {
                console.log("TextRes: ", res);
                resolve(res[0].hash);
            }).catch(err => {
                console.error(err);
                reject(err);
            });
        });
    }

    //调用区块链方法
    saveProductToBlockchain = (params, imageId, descId) => {
        console.log("params in save product: ", params);
        let auctionStartTime = Date.parse(params["product-auction-start"]) / 1000;
        let auctionEndTime = auctionStartTime + parseInt(params["product-auction-end"]) * 24 * 60 * 60;

        this.state.truffleContract.deployed().then(i => {
            //要在MetaMask中选中ganache提供的10个地址之一，from自己创建的地址会失败
            //即时获取当前地址，用该地址发交易
            let currentAccount = this.state.web3.eth.getAccounts();
                message.info("交易发起地址为: "+currentAccount, 2);

            //尝试实现事件event NewProduct监听
            i.addProductToStore(params["product-name"], params["product-category"], imageId, descId, auctionStartTime,
                    auctionEndTime, this.state.web3.utils.toWei(params["product-price"], 'ether'), parseInt(params["product-condition"]), { from: currentAccount, gas: 440000 }).then(function(f) {
                    console.log("成功调用合约的addProductToStore方法");
                    console.log(f);
            });

        });
    }



    render () {
        return (
            <div style={{display:'flex', justifyContent:'center'}}>
            <Card title="上架待拍卖商品" bordered={false} style={{ width: '80%' }} headStyle={{display:'flex', justifyContent:'center'}}>
                <Form
                    initialValues={{
                        remember: true,
                    }}
                    onFinish={this.onFinish}
                    onFinishFailed={this.onFinishFailed}
                    style={{margin:"auto", width:"60%", marginTop:"15px"}}
                >
                    <Form.Item
                        name="ProductName"
                        rules={[
                            {
                                required: true,
                                message: '请输入商品名称',
                            },
                        ]}
                    >
                        <Input placeholder="商品名称"/>
                    </Form.Item>

                    <Form.Item
                        name="ProductDesc"
                        label="商品介绍"
                        rules={[
                                {
                                    required: true,
                                    message: '请输入商品介绍',
                                },
                        ]}
                    >
                        <Input.TextArea showCount maxLength={100} />
                    </Form.Item>

                    <Form.Item
                        name="UploadPic"
                        label="上传图片"
                        valuePropName="fileList"
                        getValueFromEvent={this.normFile}
                        extra=""
                    >
                        <Upload name="logo" customRequest={this.uploadFile} listType="picture">
                            <Button icon={<UploadOutlined />}>点击上传</Button>
                        </Upload>
                    </Form.Item>

                    <Form.Item
                        name="ProductClass"
                        label="商品种类"
                        rules={[
                                {
                                    type: 'array',
                                    required: true,
                                    message: '请选择商品种类',
                                },
                            ]}
                    >
                        <Cascader placeholder="请选择" options={ProductClass} />
                    </Form.Item>


                    <Form.Item
                        name="StartPrice"
                        label="起拍价格"
                        rules={[
                                {
                                    required: true,
                                    message: '请输入起拍价格',
                                },
                            ]}
                    >
                    <InputNumber
                        addonAfter={suffixSelector}
                        style={{
                            width: '100%',
                        }}
                    />
                    </Form.Item>

                    <Form.Item name="BeginAndEndTime" label="起止时间" {...rangeConfig}>
                        <RangePicker showTime format="YYYY-MM-DD HH:mm:ss" />
                    </Form.Item>

                    <Form.Item
                        name="ProductCondition"
                        label="产品状况"
                        rules={[
                                {
                                    required: true,
                                    message: '请选择状况',
                                },
                        ]}
                    >
                        <Select placeholder="请选择">
                            <Option value="new">全新</Option>
                            <Option value="used">二手</Option>
                        </Select>
                    </Form.Item>


                    <Form.Item name="slider" label="揭示报价持续时间">
                        <Slider
                            marks={{
                                0: '0',
                                20: '20',
                                40: '40',
                                60: '60',
                                80: '80',
                                100: '100 分钟',
                            }}
                        />
                    </Form.Item>


                    <Form.Item
                        wrapperCol={{
                            offset: 10,
                            span: 16,
                        }}
                    >
                        <Button type="primary" htmlType="submit">
                            发布商品
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
        </div>
        );
    }
}

export default AuctionOut;



