import React, {Component} from "react";
import { Menu, Layout, message, Drawer, Space, Button, Divider, Form, InputNumber, Input, Select } from 'antd';
import { UserOutlined, LaptopOutlined, FileSearchOutlined, HomeOutlined, EyeTwoTone, EyeInvisibleOutlined, RocketOutlined } from '@ant-design/icons';
import cookie from "react-cookies";
//接入web3
import getWeb3 from "../../utils/getWeb3";
import getEcommerceStore from "../../utils/getEcommerceStore";
import openNotification from "../Notification";

const { Option } = Select;

const suffixSelector = (
    <Form.Item name="PriceUnit" noStyle>
      <Select
        style={{
          width: 70,
        }}
      >
        <Option selected="selected" value="ETH">ETH</Option>
        <Option value="wei">wei</Option>
      </Select>
    </Form.Item>
);



class Reveal extends Component {

    state = {
        web3 : null,
        truffleContract : null,
        visible: false
    }

    constructor (props) {
        super (props);
    }

    getBlockChainInfo = async() => {
        try{
            const web3 = await getWeb3();
            const EcommerceStore = await getEcommerceStore(web3);
            this.setState({
                web3: web3,
                truffleContract:EcommerceStore
            });
            message.success("接入智能合约成功", 2);
            console.log("Reveal 的 web3 : ");
            console.dir(web3);
            console.log("Reveal 的 EcommerceStore : ");
            console.dir(this.state.truffleContract);
            return true;
        }catch (error) {
            // Catch any errors for any of the above operations.
            alert(
              `[ERROR]接入智能合约失败.`,
            );
            console.error(error);
            return false;
          }

    }

    //componentWillMount在render之前运行
    //componentDidMount 在render之后运行
    //注意先后顺序，this.getBlockChainInfo 在前，that.renderProductDetails 在后
    async componentDidMount () {
        // componentWillMount () {
            console.log("生成Reveal模块");
            if(await this.getBlockChainInfo()){
                console.log("获得合约信息成功");
            }else{
                console.log("获得合约信息失败");
            }
        }

    showDrawer = () => {
        this.setState({
          visible: true,
        });
      };

    onClose = () => {
        this.setState({
          visible: false,
        });
      };

    //点击提交竞拍按钮，表单可以提交时
    onFinishBid = (values) => {
        console.log("揭示报价提交按钮被点击，onFinishBid 得到的数据为 : ");
        console.dir(values);
        //values 中的数据示意
        // BidSecret: "k10"
        // PriceUnit: "ETH"
        // RevealAmount: 4

        if(this.props.dataArray.length === 0){
            message.error("找不到正在竞拍的商品");
            return;
        }
        if(values.PriceUnit === undefined){
            message.error("请指定价格单位");
            return;
        }
        //把价格单位换算成wei进行比较，这里的换算写死了，后期改成 web3 中的换算方法
        console.log("揭示报价使用的价格单位 : " + values.PriceUnit);
        //单位是 wei
        let revealPrice = (values.PriceUnit === "ETH") ? (values.BidAmount*1e18) : values.BidAmount;
        console.log("揭示价 : " + revealPrice);
        let blockChainID = this.props.dataArray[0].ID;
        console.log("竞拍商品ID : " + blockChainID);

        let web3 = this.state.web3;
        //这里与 Bid 中的相同部位不同，Bid在这里加密，在传给区块链方法
        //Reveal 这里不加密，将明文传递给区块链方法

        //揭示报价信息上链
        let that = this;
        this.state.truffleContract.deployed().then(async (i) => {
            console.log("进入 this.state.truffleContract.deployed() 的回调函数");
            //测试时要在MetaMask中选中ganache提供的10个地址之一，from自己创建的地址会失败
            //即时获取当前地址，用该地址发交易
            let currentAccount = await that.state.web3.eth.getAccounts();
            message.info("交易发起地址为: "+currentAccount, 2);
            console.log("交易发起地址为: "+currentAccount);

            try{
                //调用合约的 bid 方法
                await i.revealBid(parseInt(blockChainID), revealPrice, values.BidSecret, { from: currentAccount.toString(), gas:440000 }).then(res => {
                    //注意这里调用合约方法使用的地址，必须是字符串形式，要用 toString() 转化为字符串
                    message.success("揭示报价成功");
                    console.log("成功调用合约的revealBid方法，返回 : ");
                    console.dir(res);
                    //关闭侧边栏
                    this.onClose();
                    openNotification("竞价提交成功",
                                    "您的报价: " + values.BidAmount + values.PriceUnit + ", 您的押金: " + values.SendAmount + values.PriceUnit + ", 距离揭示报价 " + this.props.dataArray[0].processTime ,
                                    'bottomLeft');
                });
            }catch(err) {
                message.error("向链上提交竞价信息失败",2);
                console.log("调用合约的bid方法失败 " + err);
                return;
            }  
        });

    }


    //点击提交竞拍按钮，表单不能提交时
    onFinishFailedBid = (errorInfo) => {
        message.warning("请正确填写竞拍信息",2);
    }








    render () {
        return (

            <div>

            <Button type="primary" icon={<RocketOutlined />} size={"large"} onClick={this.showDrawer}>
                揭示报价
            </Button>

            <Drawer
          title="揭示您的报价"
          width={400}
          onClose={this.onClose}
          visible={this.state.visible}
          bodyStyle={{ paddingBottom: 80 }}
          extra={
            <Space>
              <Button onClick={this.onClose}>取消</Button>
            </Space>
          }
        >
            <Divider />
          <Form 
            layout="vertical" hideRequiredMark
            onFinish={this.onFinishBid}
            onFinishFailed={this.onFinishFailedBid}

          >
            <Form.Item
                name="RevealAmount"
                label="揭示报价"
                rules={[
                        {
                            required: true,
                            message: '请揭示竞拍价格',
                        },
                ]}
            >
            <InputNumber
                min={0}
                placeholder="竞拍时输入的价格"
                addonAfter={suffixSelector}
                style={{
                    width: '100%',
                }}
            />
            </Form.Item>
            <Form.Item
                name="BidSecret"
                label="加密口令"
                rules={[
                        {
                            required: true,
                            message: '请与在竞价环节中输入的加密口令一致',
                        },
                ]}
            >
                <Input.Password
                placeholder="您在竞价环节中使用的加密口令"
                iconRender={visible => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
                />
            </Form.Item>
            <Form.Item>
                <Button 
                //这里不需要 onClick，不然会使函数 onFinishBid 收到参数 SyntheticBaseEvent
                // onClick={this.onFinishBid}
                type="primary" 
                htmlType="submit"
                disabled={ this.state.truffleContract === null || this.state.web3 === null } 
                >
                提交
              </Button>
            </Form.Item>

          </Form>
        </Drawer>
        </div>
        );
    }
}

export default Reveal;
