import React, {Component} from "react";
import { Menu, Layout, message, Drawer, Space, Button, Divider, Form, InputNumber, Input, Select } from 'antd';
import { UserOutlined, LaptopOutlined, FileSearchOutlined, HomeOutlined, EyeTwoTone, EyeInvisibleOutlined, RocketOutlined } from '@ant-design/icons';
import cookie from "react-cookies";
//接入web3
import getWeb3 from "../../../utils/getWeb3";
import getEcommerceStore from "../../../utils/getEcommerceStore";
import openNotification from "../../Notification";

const { Option } = Select;


class HighestBidderInfo extends Component {

    state = {
        web3 : null,
        truffleContract : null,
        highestBidderString : ""
    }

    componentDidMount = async () => {
        console.log("生成Bid模块");
        if(await this.getBlockChainInfo()){
            console.log("获得合约信息成功");
            this.getInfoFromBlockchain();
        }else{
            console.log("获得合约信息失败");
        }  
    }

   getBlockChainInfo = async() => {
        try{
            const web3 = await getWeb3();
            const EcommerceStore = await getEcommerceStore(web3);
            this.setState({
                web3: web3,
                truffleContract:EcommerceStore
            });
            console.log("Bid 的 web3 : ");
            console.dir(web3);
            console.log("Bid 的 EcommerceStore : ");
            console.dir(this.state.truffleContract);
            return true;
        }catch (error) {
            // Catch any errors for any of the above operations.
            alert(
              `[ERROR]接入智能合约失败.`,
            );
            console.error(error);
            return false;
          }

    }

    getInfoFromBlockchain = () => {
        let blockChainID = this.props.dataArray[0].ID;
        console.log("竞拍商品ID : " + blockChainID);

        let that = this;
        this.state.truffleContract.deployed().then(async (i) => {
            console.log("进入 this.state.truffleContract.deployed() 的回调函数");
            //测试时要在MetaMask中选中ganache提供的10个地址之一，from自己创建的地址会失败
            //即时获取当前地址，用该地址发交易
            let currentAccount = await that.state.web3.eth.getAccounts();
            message.info("交易发起地址为: "+currentAccount, 2);
            console.log("交易发起地址为: "+currentAccount);

            try{
                //调用合约的 highestBidderInfo 方法
                await i.highestBidderInfo(parseInt(blockChainID)).then(info => {
                    console.log("成功调用合约的highestBidderInfo方法，返回 : ");
                    console.dir(info);
                    let highestBidderString = "";
                    if (info[2].toLocaleString() == '0') {
                        //没有人揭示报价

                    } else {
                        $("#product-status").html("Auction has ended. Product sold to " + info[0] + " for " + displayPrice(info[2]) +
                            "The money is in the escrow. Two of the three participants (Buyer, Seller and Arbiter) have to " +
                            "either release the funds to seller or refund the money to the buyer");
                    }

                    message.success("查询最高竞价人信息成功",2);
                });
            }catch(err) {
                message.error("查询最高竞价人信息失败",2);
                console.log("调用合约的highestBidderInfo方法失败 " + err);
                return;
            }  
        });
    }




    render () {
        return (


            <div>
                {
                this.props.visible?(
                    <div>
                        <p>最高竞价人信息模块</p>
          <p>{this.state.highestBidderString}</p>
                        </div>
                    ):null
                }
            </div>
        );
    }
}

export default HighestBidderInfo;
