//向区块链写入商品信息
class Selling extends Component {

    state = {
        username:cookie.load('username'),
        web3 : null,
        truffleContract : null
    }

    constructor (props) {
        super(props);
    }

    getBlockChainInfo = async() => {
        try{
            const web3 = await getWeb3();
            const EcommerceStore = await getEcommerceStore(web3);
            this.setState({
                web3: web3,
                currentContract:EcommerceStore
            });
            message.success("接入智能合约成功", 8);
            return true;
        }catch (error) {
            // Catch any errors for any of the above operations.
            alert(
              `[ERROR]接入智能合约失败.`,
            );
            console.error(error);
            return false;
          }

    }

    componentDidMount () {
        console.log("生成Selling模块");
        if(this.getBlockChainInfo()){
            console.log("成功获得合约信息");
        }else{
            console.log("获得合约信息失败");
        }
    }

    onFinish = (values) => {
        console.log('Success:', values);
        //ProductName
        message.info("正在提交 Product 数据");


        axios.post('/createDevice', {
            deviceID:values.deviceID,
            deviceName:values.deviceName,
            ownerName:cookie.load('username')
        }).then(response => {
            const data = response.data;
            if(data.status === "success"){
                message.success("设备创建成功", 2);
                let deviceArray = cookie.load('deviceArray');
                cookie.remove('deviceArray', {path:'/'});
                deviceArray = [...deviceArray, values.deviceID];
                cookie.save('deviceArray', deviceArray, {path:'/'});

            }
            else if(data.status === "duplicated"){
                message.warning("该设备已被绑定", 2)
            }
            else{
                message.warning("创建设备失败", 2)
            }
        })

    }
    onFinishFailed = (errorInfo) => {
        message.warning("请正确填写商品信息",2);
    }





    render () {
        return (
            <div style={{display:'flex', justifyContent:'center'}}>
            <Card title="上架待出售商品" bordered={false} style={{ width: '80%' }} headStyle={{display:'flex', justifyContent:'center'}}>
                <Form
                    initialValues={{
                        remember: true,
                    }}
                    onFinish={this.onFinish}
                    onFinishFailed={this.onFinishFailed}
                    style={{margin:"auto", width:"60%", marginTop:"15px"}}
                >
                    <Form.Item
                        name="ProductName"
                        rules={[
                            {
                                required: true,
                                message: '请输入商品名称',
                            },
                        ]}
                    >
                        <Input placeholder="商品名称"/>
                    </Form.Item>

                    <Form.Item
                        name="ProductDesc"
                        label="商品介绍"
                        rules={[
                                {
                                    required: true,
                                    message: '请输入商品介绍',
                                },
                        ]}
                    >
                        <Input.TextArea showCount maxLength={100} />
                    </Form.Item>

                    <Form.Item
                        name="UploadPic"
                        label="上传图片"
                        valuePropName="fileList"
                        getValueFromEvent={normFile}
                        extra=""
                    >
                        <Upload name="logo" action="/upload.do" listType="picture">
                            <Button icon={<UploadOutlined />}>Click to upload</Button>
                        </Upload>
                    </Form.Item>

                    <Form.Item
                        name="ProductClass"
                        label="商品种类"
                        rules={[
                                {
                                    type: 'array',
                                    required: true,
                                    message: '请选择商品种类',
                                },
                            ]}
                    >
                        <Cascader options={ProductClass} />
                    </Form.Item>


                    <Form.Item
                        name="ProducePrice"
                        label="商品价格"
                        rules={[
                                {
                                    required: true,
                                    message: 'Please input donation amount!',
                                },
                            ]}
                    >
                    <InputNumber
                        addonAfter={suffixSelector}
                        style={{
                            width: '100%',
                        }}
                    />
                    </Form.Item>

                    <Form.Item
                        name="ProduceAmount"
                        label="商品库存"
                        rules={[
                                {
                                    required: true,
                                    message: '请输入商品库存',
                                },
                            ]}
                    >
                    <InputNumber
                        style={{
                            width: '100%',
                        }}
                    />
                    </Form.Item>

                    <Form.Item name="FinishTime" label="发货时效">
                        <Radio.Group>
                            <Radio value="1">1天</Radio>
                            <Radio value="3">3天</Radio>
                            <Radio value="7">7天</Radio>
                        </Radio.Group>
                    </Form.Item>

                    <Form.Item
                        name="ProductAddress"
                        label="发货地"
                        rules={[
                                {
                                    type: 'array',
                                    required: true,
                                    message: '请选择发货地',
                                },
                            ]}
                    >
                        <Cascader options={ProductOrigin} />
                    </Form.Item>


                    <Form.Item
                        wrapperCol={{
                            offset: 10,
                            span: 16,
                        }}
                    >
                        <Button type="primary" htmlType="submit">
                            发布商品
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
        </div>
        );
    }
}

export default Selling;





