import React, {Component} from "react";
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
// import './index.css';
import { List, Avatar, Space } from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';



const IconText = ({ icon, text }) => (
  <Space>
    {React.createElement(icon)}
    {text}
  </Space>
);



class ProductList extends Component {


  constructor (props) {
    super(props);
  }

  productListData = [];


  //应该在调用 ProductList 的函数中，将数据格式化好，在 ProductList 中直接将数据放入 render，让 render 自动刷新
  componentWillMount  = () => {

    //这里输出为 null，无所谓，因为异步，当数据准备好后，会更新render
    // console.log("即将渲染的产品为 : ");
    // console.dir(this.props.productArray);

    //模拟数据，用来测试
    for (let i = 0; i < 12; i++) {
      this.productListData.push({
        href: 'https://ant.design',
        title: '商品名称',
        avatar: 'https://joeschm/api/v1/random',
        description:
          '商品价格',
        content:
          '商品介绍',
      });
    }

  }


    render () {

      console.log("ProductList 进行了一次渲染");

        return (
            <List
    itemLayout="vertical"
    size="large"
    pagination={{
      onChange: page => {
        console.log(page);
      },
      pageSize: 5,
    }}
    dataSource={this.productListData}
    footer={
      <div>
        共 num 个
      </div>
    }
    renderItem={item => (
      <List.Item
        key={item.title}
        actions={[
          <IconText icon={StarOutlined} text="156" key="list-vertical-star-o" />,
          <IconText icon={LikeOutlined} text="156" key="list-vertical-like-o" />,
          <IconText icon={MessageOutlined} text="2" key="list-vertical-message" />,
        ]}
        extra={
          <img
            width={272}
            alt="商品图片"
            src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
          />
        }
      >
        <List.Item.Meta
          avatar={<Avatar src={item.avatar} />}
          title={<a href={item.href}>{item.title}</a>}
          description={item.description}
        />
        {item.content}
      </List.Item>
    )}
    />
        );
    }
}

export default ProductList;
